import styles from './SingleTodo.module.css'

const SingleTodo: React.FC<{text: string}> = (props) => {

  function removeItem(id) {
    props.text.slice(id)
  }

  return (
    <li onClick={(id) => removeItem(id)} className={styles.item}>{props.text}</li>
  )
}

export default SingleTodo
