import styles from './SingleTodo.module.css'

const SingleTodo: React.FC<{text: string}> = (props) => {

  function removeItem() {
    props.text.slice(0,1)
    props.text.splice()
  }

  return (
    <li onClick={() => removeItem()} className={styles.item}>{props.text}</li>
  )
}

export default SingleTodo
